        textAlign: 'center',
        backgroundColor: '#000000',
        color: "#FFF",
        flex: 2,
        color: "#FFF",

    },
    container2: {
        height: "100%",
        backgroundColor: "#000000",


    },
    paragraph3: {

        fontSize: 18,
        fontWeight: 'bold',
        textAlign: 'center',
        backgroundColor: '#000000',
        color: "#FFF",
        height: "10%",
        color: "#FFF",

    },
    paragraph4: {

        fontSize: 18,
        fontWeight: 'bold',
        textAlign: 'center',
        backgroundColor: '#000000',
        color: "#FFF",

        color: "#FFF",

    },
    section3: {

        fontSize: 18,
