        textAlign: 'center',
        backgroundColor: '#000000',
        color: "#FFF",
        height: "30%",
        color: "#FFF",
        alignContent: "center",
        padding: 20,

    },
    paragraph5: {
        fontSize: 18,
        fontWeight: 'bold',
        textAlign: 'center',
        justifyContent: "center",
        backgroundColor: '#000000',
        color: "#FFF",
        height: "10%",
        alignItems: "center",



    },
    paragraph6: {
        fontSize: 18,
        fontWeight: 'bold',
        textAlign: 'center',
        justifyContent: "center",
        backgroundColor: '#000000',
        color: "#FFf",
        height: "13%",
        alignItems: "center",
        paddingTop: 20,


    },


    section4: {
        padding: 10,
        height: "20%"

    },
});
